import React, { useEffect, useState } from "react";
import "./../league-table.css";
import TwelfthManLogo from "./../images/TwelfthManLogo.jpg";
import { Link, useLocation } from "react-router-dom";

function StandingsPage() {
  const location = useLocation();
  const [league, setLeague] = useState("pl");
  const [standingsData, setStandingsData] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(location.search);
    const leagueParam = urlParams.get("league");

    if (leagueParam) {
      setLeague(leagueParam);
      loadStandings(leagueParam);
    }
  }, [location.search]);

  const loadStandings = (league) => {
    fetch(`http://localhost:8080/standings?league=${league}`)
      .then((response) => response.json())
      .then((data) => {
        setStandingsData(data);
      })
      .catch((error) => {
        console.error(error);
      });
  };
  const createTableRows = (datas, league) => {
    return datas.map((teamData) => {
      // Create and populate the cells for each team's data
      const zones = getPositionClassName(teamData.position, league);
      applyDotClass(zones, getDescription(zones));
      const positionCell = <td className={zones}>{teamData.position}</td>;

      const teamCell = (
        <td>
          <img
            src={teamData.team.crest}
            alt={teamData.team.name}
            className="crest-image"
          />
          {teamData.team.name}
        </td>
      );

      const playedGamesCell = <td>{teamData.playedGames}</td>;
      const winsCell = <td>{teamData.won}</td>;
      const drawsCell = <td>{teamData.draw}</td>;
      const lossesCell = <td>{teamData.lost}</td>;
      const goalforCell = <td>{teamData.goalsFor}</td>;
      const goalagainstCell = <td>{teamData.goalsAgainst}</td>;
      const goaldiffCell = <td>{teamData.goalDifference}</td>;
      const pointsCell = <td>{teamData.points}</td>;

      return (
        <tr key={teamData.team.id}>
          {positionCell}
          {teamCell}
          {playedGamesCell}
          {winsCell}
          {drawsCell}
          {lossesCell}
          {goalforCell}
          {goalagainstCell}
          {goaldiffCell}
          {pointsCell}
        </tr>
      );
    });
  };

  const getPositionClassName = (position, league) => {
    let label = "";
    if (league === "pl") {
      if (position >= 1 && position <= 4) {
        label = "champions-league";
        applyDotClass(label, "Champions League");
      } else if (position >= 5 && position <= 6) {
        label = "europa-league";
        applyDotClass(label, "Europa League");
      } else if (position === 7) {
        label = "europa-conf-league-qual";
        applyDotClass(label, "Europa League Qualification");
      } else if (position >= 18 && position <= 20) {
        label = "relegation";
        applyDotClass(label, "Relegation");
      }
    } else if (league === "bundesliga") {
      if (position >= 1 && position <= 4) {
        label = "champions-league";
        applyDotClass(label, "Champions League");
      } else if (position === 5) {
        label = "europa-league";
        applyDotClass(label, "Europa League");
      } else if (position === 6) {
        label = "europa-conf-league-qual";
        applyDotClass(label, "Europa League Qualification");
      } else if (position === 16) {
        label = "relegation-qual";
        applyDotClass(label, "Relegation Qualification");
      } else if (position >= 17 && position <= 18) {
        label = "relegation";
        applyDotClass(label, "Relegation");
      }
    } else if (league === "primeradivision") {
      if (position >= 1 && position <= 4) {
        label = "champions-league";
        applyDotClass(label, "Champions League");
      } else if (position >= 5 && position <= 6) {
        label = "europa-league";
        applyDotClass(label, "Europa League");
      } else if (position === 7) {
        label = "europa-conf-league-qual";
        applyDotClass(label, "Europa League Qualification");
      } else if (position >= 18 && position <= 20) {
        label = "relegation";
        applyDotClass(label, "Relegation");
      }
    } else if (league === "seriea") {
      if (position >= 1 && position <= 4) {
        label = "champions-league";
        applyDotClass(label, "Champions League");
      } else if (position >= 5 && position <= 6) {
        label = "europa-league";
        applyDotClass(label, "Europa League");
      } else if (position === 7) {
        label = "europa-conf-league-qual";
        applyDotClass(label, "Europa League Qualification");
      } else if (position >= 18 && position <= 20) {
        label = "relegation";
        applyDotClass(label, "Relegation");
      }
    } else if (league === "ligue1") {
      if (position >= 1 && position <= 2) {
        label = "champions-league";
        applyDotClass(label, "Champions League");
      } else if (position === 3) {
        label = "champions-league-qual";
        applyDotClass(label, "Champions League Qualification");
      } else if (position === 4) {
        label = "europa-league";
        applyDotClass(label, "Europa League");
      } else if (position === 5) {
        label = "europa-conf-league-qual";
        applyDotClass(label, "Europa League Qualification");
      } else if (position >= 17 && position <= 20) {
        label = "relegation";
        applyDotClass(label, "Relegation");
      }
    }

    return label;
  };

  function applyDotClass(className, description) {
    return (
      <li>
        <span className={`dot ${className}`} />
        <span>{description}</span>
      </li>
    );
  }

  const getDescription = (zone) => {
    if (zone === "champions-league") {
      return "Champions League";
    } else if (zone === "europa-league") {
      return "Europa League";
    } else if (zone === "champions-league-qual") {
      return "Champions League Qualification";
    } else if (zone === "europa-conf-league-qual") {
      return "Europa Conference League Qualification";
    } else if (zone === "relegation-qual") {
      return "Relegation Qualification";
    } else if (zone === "relegation") {
      return "Relegation";
    }
  };

  const renderTable = () => {
    if (standingsData) {
      const leagueName = standingsData.competition.name;
      const seasonYear = standingsData.filters.season.slice(2);
      const seasonNumber = `${seasonYear}/${Number(seasonYear) + 1}`;
      const headerText = `${leagueName} - Season ${seasonNumber} Standings`;

      return (
        <div>
          <h2 className="league-table">{headerText}</h2>
          <table id="standings-table">
            <thead>
              <tr>
                <th>Position</th>
                <th>Team</th>
                <th>Played</th>
                <th>Wins</th>
                <th>Draws</th>
                <th>Losses</th>
                <th>GF</th>
                <th>GA</th>
                <th>GD</th>
                <th>Points</th>
              </tr>
            </thead>
            <tbody>
              {createTableRows(standingsData.standings[0].table, league)}
            </tbody>
          </table>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="league-table-container">
      <div id="header">
        <div className="container">
          <nav>
            <img src={TwelfthManLogo} alt="TwelfthManLogo" />
