import React, { useEffect, useState } from "react";
import "./../league-table.css";

function StandingsPage() {
  const [league, setLeague] = useState("pl");
  const [standingsData, setStandingsData] = useState(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const leagueParam = urlParams.get("league");

    if (leagueParam) {
      setLeague(leagueParam);
    }

    loadStandings(leagueParam || "pl");
  }, []);

  const loadStandings = (league) => {
    fetch(`http://localhost:8080/standings?league=${league}`)
      .then((response) => response.json())
      .then((data) => {
        setStandingsData(data);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  const createTableRows = (datas, league) => {
    return datas.map((teamData) => {
      // Create and populate the cells for each team's data
      const positionCell = (
        <td className={getPositionClassName(teamData.position, league)}>
          {teamData.position}
        </td>
      );

      const teamCell = (
        <td>
          <img
            src={teamData.team.crest}
            alt={teamData.team.name}
            className="crest-image"
          />
          {teamData.team.name}
        </td>
      );

      const playedGamesCell = <td>{teamData.playedGames}</td>;
      const winsCell = <td>{teamData.won}</td>;
      const drawsCell = <td>{teamData.draw}</td>;
      const lossesCell = <td>{teamData.lost}</td>;
      const goalforCell = <td>{teamData.goalsFor}</td>;
      const goalagainstCell = <td>{teamData.goalsAgainst}</td>;
      const goaldiffCell = <td>{teamData.goalDifference}</td>;
      const pointsCell = <td>{teamData.points}</td>;

      return (
        <tr key={teamData.team.id}>
          {positionCell}
          {teamCell}
          {playedGamesCell}
          {winsCell}
          {drawsCell}
          {lossesCell}
          {goalforCell}
          {goalagainstCell}
          {goaldiffCell}
          {pointsCell}
        </tr>
      );
    });
  };

  const getPositionClassName = (position, league) => {
    if (league === "pl") {
      if (position >= 1 && position <= 4) {
        return "champions-league";
      } else if (position >= 5 && position <= 6) {
        return "europa-league";
      } else if (position === 7) {
        return "europa-conf-league-qual";
      } else if (position >= 18 && position <= 20) {
        return "relegation";
      }
    } else if (league === "bundesliga") {
      if (position >= 1 && position <= 4) {
        return "champions-league";
      } else if (position === 5) {
        return "europa-league";
      } else if (position === 6) {
        return "europa-conf-league-qual";
      } else if (position === 16) {
        return "relegation-qual";
      } else if (position >= 17 && position <= 18) {
        return "relegation";
      }
    } else if (league === "primeradivision") {
      if (position >= 1 && position <= 4) {
        return "champions-league";
      } else if (position >= 5 && position <= 6) {
        return "europa-league";
      } else if (position === 7) {
        return "europa-conf-league-qual";
      } else if (position >= 18 && position <= 20) {
        return "relegation";
      }
    } else if (league === "seriea") {
      if (position >= 1 && position <= 4) {
        return "champions-league";
      } else if (position >= 5 && position <= 6) {
        return "europa-league";
      } else if (position === 7) {
        return "europa-conf-league-qual";
      } else if (position >= 18 && position <= 20) {
        return "relegation";
      }
    } else if (league === "ligue1") {
      if (position >= 1 && position <= 2) {
        return "champions-league";
      } else if (position === 3) {
        return "champions-league-qual";
      } else if (position === 4) {
        return "europa-league";
      } else if (position === 5) {
        return "europa-conf-league-qual";
      } else if (position >= 17 && position <= 20) {
        return "relegation";
      }
    }

    return "";
  };

  const renderTable = () => {
    if (standingsData) {
      const leagueName = standingsData.competition.name;
      const seasonYear = standingsData.filters.season.slice(2);
      const seasonNumber = `${seasonYear}/${Number(seasonYear) + 1}`;
      const headerText = `${leagueName} - Season ${seasonNumber} Standings`;

      return (
        <table id="standings-table">
          <thead>
            <tr>
              <th>Position</th>
              <th>Team</th>
              <th>Played</th>
              <th>Wins</th>
              <th>Draws</th>
              <th>Losses</th>
              <th>GF</th>
              <th>GA</th>
              <th>GD</th>
              <th>Points</th>
            </tr>
          </thead>
          <tbody>
            {createTableRows(standingsData.standings[0].table, league)}
          </tbody>
        </table>
      );
    }
    return null;
  };

  return (
    <div id="header">
      <div class="container">
        <nav>
          <img src="/images/TwelfthManLogo.jpg" class="logo" />
          <ul>
            <li>
              <a href="opening-page.html">Home</a>
            </li>
            <li>
              <a href="predictions.html">Predictions</a>
            </li>
            <li>
              <a href="fixtures.html">Fixtures</a>
            </li>
            <li class="dropdown">
              <a href="#" class="dropdown-toggle">
                League Tables
              </a>
              <ul class="dropdown-menu">
                <li>
                  <a href="league-table.html?league=pl">Premier League</a>
                </li>
                <li>
                  <a href="league-table.html?league=bundesliga">Bundesliga</a>
                </li>
                <li>
                  <a href="league-table.html?league=primeradivision">
                    Primera Division
                  </a>
                </li>
                <li>
                  <a href="league-table.html?league=seriea">Serie A</a>
                </li>
                <li>
                  <a href="league-table.html?league=ligue1">Ligue 1</a>
                </li>
              </ul>
            </li>
          </ul>
        </nav>
        <h1 id="league-header">{renderTable()}</h1>
        <ul id="zone-labels"></ul>
      </div>
    </div>
  );
}

export default StandingsPage;
