import React from "react";
import "./../opening-page-styles.css";
import TwelfthManLogo from "./../images/TwelfthManLogo.jpg";
import OpeningVideo from "./../images/Opening video.mp4";

function OpeningPage() {
  const redirectToLeagueTable = (event) => {
    const selectedOption = event.target.value;

    // Redirect to the corresponding league table HTML page based on the selected option
    switch (selectedOption) {
      case "PL":
        window.location.href = "league-table.html?league=pl";
        break;
      case "Bundesliga":
        window.location.href = "league-table.html?league=bundesliga";
        break;
      case "Primera Division":
        window.location.href = "league-table.html?league=primeradivision";
        break;
      case "Serie A":
        window.location.href = "league-table.html?league=seriea";
        break;
      case "Ligue 1":
        window.location.href = "league-table.html?league=ligue1";
        break;
      default:
        // Handle default case or do nothing
        break;
    }
  };

  return (
    <div className="opening page-container">
      <img src={TwelfthManLogo} alt="TwelfthManLogo" />

      <div className="bg-overlay"></div>
      <video className="opening-video" id="myVideo" autoPlay muted loop>
        <source src={OpeningVideo} />
      </video>

      <div className="content">
        <br />
        <br />
        <br />
        <h1>Twelfth Man</h1>
        <p>
          <i>Your All-In-One Football Companion</i>
        </p>

        <nav>
          <ul>
            <li>
              <a href="predictions.html">Predictions</a>
            </li>
            <li>
              <a href="fixtures.html">Fixtures</a>
            </li>
            <li className="dropdown">
              <select
                name="league-tables"
                id="league-tables"
                onChange={redirectToLeagueTable}
              >
                <option value="" disabled>
                  League Tables
                </option>
                <option value="PL">Premier League</option>
                <option value="Bundesliga">Bundesliga</option>
                <option value="Primera Division">Primera Division</option>
                <option value="Serie A">Serie A</option>
                <option value="Ligue 1">Ligue 1</option>
              </select>
            </li>
          </ul>
        </nav>
      </div>
    </div>
  );
}

export default OpeningPage;
