import React, { useEffect, useState } from "react";
import "./../league-table.css";

const Standings = () => {
  const [standings, setStandings] = useState([]);
  const [leagueName, setLeagueName] = useState("");
  const [seasonNumber, setSeasonNumber] = useState("");

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const league = urlParams.get("league");
    let defaultLeague = "pl";

    if (league === "bundesliga") {
      defaultLeague = "bundesliga";
    } else if (league === "primeradivision") {
      defaultLeague = "primeradivision";
    } else if (league === "seriea") {
      defaultLeague = "seriea";
    } else if (league === "ligue1") {
      defaultLeague = "ligue1";
    }

    loadStandings(defaultLeague);
  }, []);

  const loadStandings = async (league) => {
    try {
      const response = await fetch(`http://localhost:3000/standings/${league}`);
      const data = await response.json();

      const leagueName = data["competition"]["name"];
      const seasonYear = data["filters"]["season"].slice(2);
      const seasonNumber = `${seasonYear}/${Number(seasonYear) + 1}`;

      setStandings(data["standings"][0]["table"]);
      setLeagueName(leagueName);
      setSeasonNumber(seasonNumber);
    } catch (error) {
      console.error(error);
    }
  };

  const applyDotClass = (className, description) => {
    const zoneLabels = document.querySelector("#zone-labels");

    // Check if a dot with the given class already exists
    const existingDot = zoneLabels.querySelector(`.${className}`);
    if (existingDot) {
      return; // Skip creating the dot if it already exists
    }

    const zoneLabel = document.createElement("li");
    const dotSpan = document.createElement("span");
    dotSpan.classList.add("dot", className);
    zoneLabel.appendChild(dotSpan);

    const descriptionSpan = document.createElement("span");
    descriptionSpan.textContent = description;
    zoneLabel.appendChild(descriptionSpan);
    zoneLabels.appendChild(zoneLabel);
  };

  return (
    <div className="league-table-container">
      <div id="header">
        <div className="container">
          <nav>
            <img src="/../images/TwelfthManLogo.jpg" className="logo" />
            <ul>
              <li>
                <a href="opening-page.html">Home</a>
              </li>
              <li>
                <a href="predictions.html">Predictions</a>
              </li>
              <li>
                <a href="fixtures.html">Fixtures</a>
              </li>
              <li class="dropdown">
                <a href="#" class="dropdown-toggle">
                  League Tables
                </a>
                <ul className="dropdown-menu">
                  <li>
                    <a href="league-table.html?league=pl">Premier League</a>
                  </li>
                  <li>
                    <a href="league-table.html?league=bundesliga">Bundesliga</a>
                  </li>
                  <li>
                    <a href="league-table.html?league=primeradivision">
                      Primera Division
                    </a>
                  </li>
                  <li>
                    <a href="league-table.html?league=seriea">Serie A</a>
                  </li>
                  <li>
                    <a href="league-table.html?league=ligue1">Ligue 1</a>
                  </li>
                </ul>
              </li>
            </ul>
          </nav>
          <h2 id="league-header">
            {leagueName} - Season {seasonNumber} Standings
          </h2>
          <table id="standings-table">
            <thead>
              <tr>
                <th>Position</th>
                <th>Team</th>
                <th>Played Games</th>
                <th>Wins</th>
                <th>Draws</th>
                <th>Losses</th>
                <th>Goals For</th>
                <th>Goals Against</th>
                <th>Goal Difference</th>
                <th>Points</th>
              </tr>
            </thead>
            <tbody>
              {standings.map((teamData) => (
                <tr key={teamData.team.id}>
                  <td
                    className={
                      (
                        leagueName === "pl"
                          ? teamData.position >= 1 && teamData.position <= 4
                          : teamData.position >= 1 && teamData.position <= 2
                      )
                        ? "champions-league"
                        : teamData.position >= 5 && teamData.position <= 6
                        ? "europa-league"
                        : teamData.position === 7
                        ? "europa-conf-league-qual"
                        : ""
                    }
                  >
                    {teamData.position}
                  </td>
                  <td>{teamData.team.name}</td>
                  <td>{teamData.playedGames}</td>
                  <td>{teamData.won}</td>
                  <td>{teamData.draw}</td>
                  <td>{teamData.lost}</td>
                  <td>{teamData.goalsFor}</td>
                  <td>{teamData.goalsAgainst}</td>
                  <td>{teamData.goalDifference}</td>
                  <td>{teamData.points}</td>
                </tr>
              ))}
            </tbody>
          </table>
          <ul id="zone-labels"></ul>
        </div>
      </div>
    </div>
  );
};

export default Standings;
